/*=============================================================================
	Draw string
=============================================================================*/
void DrawText(u8* str, u8 strLen, u8 xStart, u8 yStart, u8 fontType, u8 align)
{
	u8 i=0;
	Font_t* font;
	u8 totalWidth;
	
	//find font
	font = GetFont(fontType);

	//compute alignment
	if (align != ALIGN_LEFT)
	{
		totalWidth = GetTextWidth(str, strLen, font);
		if (align == ALIGN_RIGHT)
		{
			//align right
			xStart = xStart - totalWidth;
		}
		else {
			//align center
			xStart = xStart - totalWidth/2;
		}
	}
	
	//draw characters
	for (i=0; i<strLen; i++)
	{
		xStart += font->interCharWidth + DrawChar(xStart, yStart, str[i], font);
	}
}

