/*=============================================================================
  Draw multiline text
================================================================================*/
void DrawMultilineText(u8 *text, u8 textLength)
{
	const u8 nbMaxLines = 10;
	const u8 interLineHeight = 2;
	u8 i=0, lastSeparator=0, lineWidth=0, lineStart=0, nLines=0;
	Font_t *font;
	u8 lineLengths[nbMaxLines];
	u8 marginTop=0;
	font = GetFont(FONT_ROBOTO24);
	
	//init array
	for (i=0; i<nbMaxLines; i++)
	{
		lineLengths[i] = 0;
	}
	
	//split line at words (separators: space or comma)
	lineStart = 0;
	nLines = 0;
	lineWidth = 0;
	i = 0;
	while (i<textLength)
	{
		if ((text[i] < font->firstChar) && (text[i] > font->lastChar))
		{
			i++;
		}
		else {
				
			lineWidth += font->widths[text[i] - font->firstChar];	//add char width
			lineWidth += font->interCharWidth ;	//add interchar space
			
			if ((text[i] == 0x20) || (text[i] == 0x2c))
			{
				lastSeparator = i;
			}
			
			if (lineWidth > 128)
			{
				if (lastSeparator == 0)
				{
					lineLengths[nLines] = i - 1 -  lineStart;
					nLines++;
					lineWidth = 0;
					lineStart = i;
					i++;
				}
				else
				{
					lineLengths[nLines] = lastSeparator - lineStart;
					nLines++;
					lineStart = lastSeparator;
					lineWidth = 0;
					i = lineStart;
				}
			}
			else {
				i++;
			}
		}
		
		if (nLines > nbMaxLines)
			break;
	}
	
	
	//add remaining characters
	if (i - 1 > lineStart)
	{
		lineLengths[nLines] = textLength - lineStart;
		nLines++;
	}		
	
	//compute top margin 
	if (nLines > 1)
	{
		marginTop = (128 - nLines * font->charHeight  - interLineHeight * (nLines - 1))/2;
	}
	else 
	{
		marginTop = (128 - font->charHeight)/2;
	}
	
	//draw lines
	lineStart = 0;
	for (i=0; i<nLines; i++)
	{
		DrawText(text + lineStart, lineLengths[i], 64, marginTop + (24 + interLineHeight)*i, FONT_ROBOTO24, ALIGN_CENTER);
		lineStart += lineLengths[i];
	}
}


/*=============================================================================
	Compute string total length
=============================================================================*/
static u8 GetTextWidth(u8 *text, u8 textLength, Font_t *font)
{
	u8 totalLen = 0;
	u8 i=0;
	
	for (i=0; i< textLength; i++)
	{
		if ((text[i] >= font->firstChar) && (text[i] <= font->lastChar)) 
			totalLen += font->widths[text[i] - font->firstChar];
	}
	totalLen += (textLength - 1) * font->interCharWidth;
	
	return totalLen;
}

/*=============================================================================
	DRAW SINGLE CHAR
=============================================================================*/
u8 DrawChar(u8 xStart, u8 yStart, char character, Font_t* font) 
{
	u16 dataOffset, dataOrigin;
	u8 width = 0;
	u8 i;
	u8 charIndex = ((u8) character) - font->firstChar;
	
	//prevent unknown chars to be processed
	if ((character < font->firstChar) || (character > font->lastChar)) 
		return 0;
	
	width = font->widths[charIndex];	
	dataOrigin = charIndex * font->charWidth * font->charHeight/8;
	
	for (i=0; i < width; i++)
	{
		u16 Pixels16 = 0;
		dataOffset = dataOrigin + i*font->charHeight/8;
		
		Pixels16 = (font->data[dataOffset] << 8) |  font->data[dataOffset + 1];
		Set16PixelsAngle0(xStart + i, yStart, Pixels16);
		
		if (font->charHeight > 16)
			Pixels16 = font->data[dataOffset + 2 ] << 8; 
		if (font->charHeight > 24)
			Pixels16 |= font->data[dataOffset + 3 ] ;
		if (font->charHeight > 16)		
			Set16PixelsAngle0(xStart + i , yStart + 16, Pixels16);
	}
	return width;			
}



/***********************************************************
* Slow function
* Draw a text with preset angle (0,90, 180, 270)
* deux positions possibles : top left avec alignement  gauche ou bottom 
************************************************************/
void DrawTextAngle(char* str, Font_t* font, u8 align, u8 y, u8 margin, u8 angle) 
{
	u8 i,x, ascVal, width;
	u16 totalWidth;
	bool isFirst = true;
	
	//première passe pour calculer la largeur totale
	i = 0;
	totalWidth = 0;
	while (str[i] != '\0')
	{
		ascVal = (u8) str[i];
		if ((ascVal >= font->firstChar) && (ascVal < font->lastChar)) //is it a text ?
		{
			if (isFirst) {
				isFirst = false;
			}
			else {
				totalWidth += font->interCharWidth;
			}
			totalWidth += font->widths[ascVal - font->firstChar];
		}
		
		i++;
	}
	if (align == ALIGN_LEFT) {
		x = margin;
	}
	else if (align == ALIGN_RIGHT) {
		x = (u8) (128 - totalWidth - margin);
	}
	else
	{
		//center
		x = (u8) (64 - totalWidth/2.0);
	}

	if (x > 127)
		x = 0;
	if (y > 127)
		y = 0;
		
	
	//seconde passe pour afficher les caractres, un par un (RAM-friendly)
	isFirst = true;
	i = 0;
	while (str[i] != '\0')
	{
		ascVal = (u8) str[i];

		if ((ascVal >= font->firstChar) && (ascVal <= font->lastChar)) //is it a text ?
		{
			if (isFirst) {
				isFirst = false;
			}
			else {
				//DrawEmpty(x, y, font->interCharWidth, font->charHeight);
				x+= font->interCharWidth;
			}
			
			//charIndex = ascVal - font->firstChar;							//on trouve la position du caractre dans notre tableau

			width = DrawCharAngle(x,y, ascVal, font, angle) ;
			x += width;
		}
		
		i++;
	}
}


/***********************************************************
* Slow function
* Draw a character with preset angle (0,90, 180, 270)
************************************************************/
u8 DrawCharAngle(u8 xStart, u8 yStart, u8 character, Font_t* font, u8 angle) 
{
	u8 width, i, j;
	u16 dataOffset, dataOrigin;
	u8 charIndex;
	width = 0;
	
	charIndex = ((u8) character) - font->firstChar;
	
	//prevent unknown chars to be processed
	if ((character < font->firstChar) || (character > font->lastChar)) 
		return 0;
	
	width = font->widths[charIndex];	
	dataOrigin = charIndex * font->charWidth * font->charHeight/8;
	
	for (i=0; i < width; i++)
	{
		u16 Pixels16 = 0;
		dataOffset = dataOrigin + i*font->charHeight/8;
		Pixels16 = (font->data[dataOffset] << 8) |  font->data[dataOffset + 1];
		
		for (j=0; j<16; j++)
			SetPixel(xStart + i, yStart + j, (Pixels16>>(15-j)) & 0x1 , angle);
		
		if (font->charHeight > 16)
			Pixels16 = font->data[dataOffset + 2 ] << 8; 
		if (font->charHeight > 24)
			Pixels16 |= font->data[dataOffset + 3 ] ;

		if (font->charHeight > 16)
			for (j=0; j<16; j++)
				SetPixel(xStart + i, yStart + 16 + j, (Pixels16>>(15-j)) & 0x1 , angle);
	}
	return width;
}


