{
	u16 dataOffset, dataOrigin;
	u8 width = 0;
	u8 i;
	Font_t *font = &roboto24;
	u8 charIndex = ((u8) character) - font->firstChar;
	
	//prevent unknown chars to be processed
	if ((character < font->firstChar) || (character > font->lastChar)) 
		return 0;
	
	width = font->widths[charIndex];	
	dataOrigin = charIndex * font->charWidth * font->charHeight/8;
	
	for (i=0; i < width; i++)
	{
		u16 Pixels16 = 0;
		dataOffset = dataOrigin + i*font->charHeight/8;
		
		Pixels16 = (BitReverseTable256[font->data[dataOffset+2]] << 8) |  BitReverseTable256[font->data[dataOffset+1]];
		if (Pixels16 != 0)
			Set16PixelsAngle0(127 - (xStart + i), 127 - yStart - 24, Pixels16);
		
		Pixels16 = (BitReverseTable256[font->data[dataOffset+0]] << 8);
		if (Pixels16 != 0)
			Set16PixelsAngle0(127 - (xStart + i), 127 - yStart +16 - 24, Pixels16);
	}
	return width;	
}



/*===============================================================================
  Return font handle
*================================================================================*/
Font_t* GetFont(u8 fontType)
{
	Font_t *font;
	
	//find font
	if (fontType == FONT_SONYSKETCH32)
		font = &sonysketch32; 
	else if (fontType == FONT_SMALLICONS)
		font = &smallIcons;
	else if (fontType == FONT_ROBOTO24)
		font = &roboto24;
	
	return font;
	
}


/*=============================================================================
	DRAW IMAGE
=============================================================================*/
void DrawImageFast(u8 xStart, u8 yStart, char character) 
{
	
	u16 dataOffset, dataOrigin;
	u8 width = 0;
	u8 i;
	u8 nBytes;
	Font_t* font = &bigIcons;
	u8 charIndex = character;
	u8 j;

	if (character > font->lastChar)
		return;
	
	dataOrigin = 0;
	for (i=font->firstChar; i<character; i++)
	{
		dataOrigin += font->widths[i] * font->charHeight/8;
		
	}
	
	width = font->widths[charIndex];	
	//dataOrigin = charIndex * font->charWidth * font->charHeight/8;
	nBytes = font->charHeight/8;
	for (i=0; i < width; i++)
	{
		
		u16 Pixels16 = 0;
		dataOffset = dataOrigin + i*nBytes;
		
		for (j=0; j<nBytes; j=j+2)
		{
			Pixels16 = (font->data[dataOffset + j ] << 8) |  font->data[dataOffset + j + 1];
			if (Pixels16 != 0)
				Set16PixelsAngle0(xStart + i, yStart + 8*j, Pixels16);
		}
	}
	
}

