/**********************************************
// convertit un float en string
// si la valeur est supérieur à 999, on n'affiche pas la décimale
// les valeurs inférieures à 0 ou supérieures à 1999 ne sont pas affichées (...)
***********************************************/
char* floatToString(char* strBuffer, float number)
{
	u8 i = 0;
	int num;
	
	char* pStr = strBuffer + sizeof(strBuffer) -1 ;
	strBuffer[7] = '\0';
	//on n'affiche pas de nombre négatif ni de nombre > 1999
	if (number < 0 || number > 1999)
	{
			strBuffer[6] = '.';
			strBuffer[5] = '.';
			strBuffer[4] = '.';
			pStr = strBuffer  + 4;
	}
	else
	{
		u8 decimal;
		i = 6;
		if (number <100)	//on affiche la décimale
		{
				decimal = (((int) (number*10)) % 10);
			  strBuffer[6] = decimal + '0';
				strBuffer[5] = '.';
				i-=2;
		}
		num = (int) number;
		while (num != 0) { 
        strBuffer[i--] = (num % 10) + '0';
        num /= 10;
    }
		pStr = strBuffer  + i + 1;
	}
	 
	return pStr;
}

